#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="D:\Workspaces\Miscellaneous\backendPainter\node_modules\grunt\bin\node_modules;D:\Workspaces\Miscellaneous\backendPainter\node_modules\grunt\node_modules;D:\Workspaces\Miscellaneous\backendPainter\node_modules;D:\Workspaces\Miscellaneous\node_modules;D:\Workspaces\node_modules;D:\node_modules;D:\Workspaces\Miscellaneous\backendPainter\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/Workspaces/Miscellaneous/backendPainter/node_modules/grunt/bin/node_modules:/mnt/d/Workspaces/Miscellaneous/backendPainter/node_modules/grunt/node_modules:/mnt/d/Workspaces/Miscellaneous/backendPainter/node_modules:/mnt/d/Workspaces/Miscellaneous/node_modules:/mnt/d/Workspaces/node_modules:/mnt/d/node_modules:/mnt/d/Workspaces/Miscellaneous/backendPainter/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../grunt/bin/grunt" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../grunt/bin/grunt" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../grunt/bin/grunt" $args
  } else {
    & "node$exe"  "$basedir/../grunt/bin/grunt" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
